query LatestBlogPosts($host: String!, $posts: Int!) {
  Hashnode {
    hn_publication(host: $host) {
      posts(first: $posts) {
        edges {
          node {
            id
            title
            content {
              text
            }
            tags {
              name
            }
            coverImage {
              url
            }
            author {
              id
              username
              name
              profilePicture
            }
            publishedAt
            readTimeInMinutes
          }
        }
      }
    }
  }
}
